import React from 'react'
import { useCart } from "use-cart";
import Catalogue from '../Data/Catalogue';

function Cart() {
const { addItem, removeItem, removeLineItem, clearCart } = useCart()
    return (
            <div>
                {
                    Catalogue.map((item) => {
                        return(
                            <div>
                                {item.stock}-{item.quantity}{" "}
                                <button className="" onClick={() =>{addItem(item.stock)}}>Increase Quantity</button>
                                <button className="" onClick={() =>{removeItem(item.stock)}}>Decrease Quantity</button>
                                <button className="" onClick={() =>{removeLineItem(item.stock)}}>Remove Item</button>
                            </div>
                        )
                    })
                }
                <button onClick={clearCart}>Remove From Cart</button>
            </div>
    )
}

export default Cart
