import React from 'react'
import { useCart } from "use-cart";
import Catalogue from '../Data/Catalogue';

function Cart() {
const { items, addItem, removeItem, removeLineItem, clearCart } = useCart()
    return (
            <div>
                {
                    items.map((item) => {
                        return(
                            <div>
                                {item.sku}-{item.quantity}{" "}
                                <button className="" onClick={() =>{addItem(item.sku)}}>Increase Quantity</button>
                                <button className="" onClick={() =>{removeItem(item.sku)}}>Decrease Quantity</button>

                            </div>
                        )
                    })
                }
            </div>
    )
}

export default Cart
