import React from 'react'
import Catalogue from '../Data/Catalogue'
import "./Home.css"
import { useCart } from "use-cart";

function CatalogueCards() {
    const { addItem } = useCart()

    return (
        <div className="catalogue_card">
            <div className="catalogue_org">
                {
                    Catalogue.map((item) => {
                        return(
                            <div className="catalogue_main">
                                <div className="catalogue_img">
                                    <img className="catalogue_img" src={item.imgurl} alt="JackDaniels" ></img>
                                </div>
                                <div className="item_desc">
                                <div className="item_div">
                                    <h3 className="item_name">Name: {item.Name}</h3>
                                </div>
                                <div className="item_div">
                                    <h3 className="item_name">Price: {item.price}</h3>
                                </div>
                                <div className="item_div">
                                    <h3 className="item_name">In-Stock: {item.stock}</h3>
                                </div>
                                <div className="item_button">
                                <button className="add_to_cart" onClick={() => {
                                  addItem("TEST_SKU")  
                                }}>Add To Cart</button>
                                </div>
                                </div>
                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default CatalogueCards
