import axios from 'axios';
import React, {useState} from 'react'

function Login() {

    const [username, setUserName] = useState("");
    const [password, setPassword] = useState("");

    const handleLogin = (event) =>{
        event.preventDefault();
        axios.post("http://127.0.0.1:8000/token-auth/")
        .then((response) => response.send(response.data))
        //.then(console.log(response))

    }
    const handleChange = (event) =>{
        event.preventDefault();
        axios.post("http://127.0.0.1:8000/token-auth/")
        .then((response) => response.send(response.data))
        //.then(console.log(response))

    }

    return (
        <div>
            <div>
                <input type="text" placeholder='username' onChange={ handleChange }></input><br />
                <input type="password" placeholder='password' onChange={ handleChange }></input><br />
                <button type="submit" onClick={handleLogin}>Login</button>
            </div>
        </div>
    )
}

export default Login
