import axios from 'axios';
import React, {useState} from 'react'

function Login() {

    const [username, setUserName] = useState("");
    const [password, setPassword] = useState("");
    const [displayedForm, setDisplayedForm] = useState("");
    const [user, setUser] = useState(false);



    const handleLogin = (event, data) =>{
        event.preventDefault();
        const RequestData = {
            method:"POST",
            headers: { 'Content-Type': "application/x-www-form-urlencoded"},
            body: JSON.stringify({
                    username : username,
                    password : password
            })
        }
        fetch('http://127.0.0.1:8000/api/token-auth/', RequestData)
        .then(response => response.json())
        .then((data) => {
            localStorage.setItem('token', JSON.stringify(data.token));
        })
        }

    const handleLogout = () => {
        localStorage.removeItem('token');
    };

    const handleChangeUserName = (event) =>{
        event.preventDefault();
        setUserName(
            event.target.value
        )
    }
    console.log(username)
    const handleChangePassword = (event) =>{
        event.preventDefault();
        setPassword(
            event.target.value
        )
    }
    console.log(password)



    return (
        <div>
            <div>
                <input type="text" placeholder='username' onChange={ handleChangeUserName }></input><br />
                <input type="password" placeholder='password' onChange={ handleChangePassword }></input><br />
                <button type="submit" onClick={handleLogin}>Login</button>
                <button type="submit" onClick={handleLogout}>Log Out</button>
            </div>
        </div>
    )
}

export default Login
